package com.calssofmay;

public class ForLoopDemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/// Sum numbers 1 to 10

		int sum = 0;

		for (int i = 0; i <= 10; i++) {

			sum = sum + i;
		}

		System.out.println("The total sum is :" + sum);
	}

}
